-- ----------------------------
-- Table structure for task_execution_statistics
-- ----------------------------
DROP TABLE IF EXISTS `task_execution_statistics`;
CREATE TABLE `task_execution_statistics`  (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `task_id` int NOT NULL COMMENT '任务ID',
  `execution_id` varchar(64) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_as_cs NOT NULL COMMENT '执行ID，用于标识单次执行',
  `start_time` datetime NOT NULL COMMENT '开始时间',
  `end_time` datetime NULL DEFAULT NULL COMMENT '结束时间',
  `total_urls` int NULL DEFAULT 0 COMMENT '总URL数量',
  `success_urls` int NULL DEFAULT 0 COMMENT '成功抓取URL数量',
  `failed_urls` int NULL DEFAULT 0 COMMENT '失败URL数量',
  `total_articles` int NULL DEFAULT 0 COMMENT '总文章数量',
  `success_articles` int NULL DEFAULT 0 COMMENT '成功入库文章数量',
  `failed_articles` int NULL DEFAULT 0 COMMENT '失败文章数量',
  `total_download_time` bigint NULL DEFAULT 0 COMMENT '总下载耗时(毫秒)',
  `avg_download_time` bigint NULL DEFAULT 0 COMMENT '平均下载耗时(毫秒)',
  `status` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_as_cs NULL DEFAULT 'running' COMMENT '执行状态：running, completed, failed',
  `error_message` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_as_cs NULL COMMENT '错误信息',
  `created_time` datetime NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_time` datetime NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `idx_task_id`(`task_id` ASC) USING BTREE,
  INDEX `idx_execution_id`(`execution_id` ASC) USING BTREE,
